import { Injectable } from '@angular/core';
import {Ilogin} from "../interfaces/login";
import {ISession} from "../interfaces/session";
import {BACKEND_URL} from "../constants/backend";

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor() { }


  private loggedIn: boolean = false;

  async login(authentication: Ilogin): Promise<boolean> {
    const res = await fetch(BACKEND_URL + '/api/authentication/authenticate', {
      method: "POST",
      body: JSON.stringify(authentication),
      headers: { 'Content-Type': 'application/json' }
    })
    const token = await fetch(BACKEND_URL + '/api/authentication/authenticate', {
      method: "POST",
      body: JSON.stringify(authentication),
      headers: {'Content-Type': 'application/json'}
    })
      .then(r => r.text())
      .then((token) => {
        return token
      }).catch((error) => {
        console.error('Error:', error);
      });

    console.log(token, 'hola token')
    
    debugger;
     if(!res.ok) return false
     const token2 = res.text();
     console.log(token)
     if (!token2) return false;

    this.setSession(token2);
    return true;
  }

  isLoggedIn(){
    return this.loggedIn;
  }

  getSession(): ISession {
    const item: string = localStorage.getItem('session') || 'invalid';
    if (item !== 'invalid') {
      return JSON.parse(item);
    }
    return { expiresIn: '', token: '' };
  }

  setSession(token: any, expiresTimeHours: number = 24) {
    const date = new Date();
    date.setHours(date.getHours() + expiresTimeHours);

    const session: ISession = {
      expiresIn: new Date(date).toISOString(),
      token,
    };

    localStorage.setItem('session', JSON.stringify(session));
    //window.location.reload();
  }

  async getMe() {
    const res = await fetch('', {
      headers: {
        Authorization: this.getSession().token!,
      },
    });
    return await res.json();
  }

  resetSession() {
    localStorage.removeItem('session');
    this.loggedIn = false;
    window.location.reload();
  }
}
